  title,
  onPress,
  variant = 'primary',
  size = 'medium',
  disabled = false,
  loading = false,
}) => {
  const buttonStyle: ViewStyle[] = [
    styles.base,
    styles[size],
    styles[variant],
    (disabled || loading) && styles.disabled,
  ];

